mapper.select:


		    SELECT
		  		AUTH_SEQ, UP_AUTH_SEQ, AUTH_CD, AUTH_NM, AUTH_TYPE_CD, USE_YN,
		       (SELECT COUNT(AUTH_SEQ) FROM TB_AUTH_SAMPLE   WHERE  UP_AUTH_SEQ  =  A.AUTH_SEQ


			AND USE_YN = #{useYn}


		       ) SUB_AUTH_CNT,
		       (SELECT AUTH_NM FROM  TB_AUTH_SAMPLE    WHERE   AUTH_SEQ  =  A.UP_AUTH_SEQ) UP_AUTH_NM
            FROM TB_AUTH_SAMPLE A
            WHERE UP_AUTH_SEQ = #{authSeq}



			AND USE_YN = #{useYn}

